import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Signup from './components/Signup';
import { useTasks } from './hooks/useTasks';
import AddTask from './components/AddTask';
import TaskList from './components/TaskList';
import { Box, Typography, CircularProgress, Alert, Button } from '@mui/material';
import { Task } from './models/Task';

const App: React.FC = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const { tasks, addTask, updateTask, toggleTask, deleteTask, isLoading } = useTasks();

  if (!isAuthenticated) {
    return (
      <Router>
        <Routes>
          <Route path="/login" element={<Login onLogin={() => setIsAuthenticated(true)} />} />
          <Route path="/signup" element={<Signup onSignup={() => {}} />} />
          <Route path="*" element={<Navigate to="/login" />} />
        </Routes>
      </Router>
    );
  }

  return (
    <Box sx={{ maxWidth: 800, mx: 'auto', p: 3 }}>
      <Typography variant="h4" component="h1" gutterBottom sx={{ mb: 4 }}>
        To-Do
      </Typography>
      <Button onClick={() => setIsAuthenticated(false)} sx={{ mb: 2 }}>Logout</Button>
      <AddTask onAdd={async (title) => { await addTask({ title, completed: false }); }} />
      {isLoading ? (
        <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
          <CircularProgress />
        </Box>
      ) : tasks?.length === 0 ? (
        <Alert severity="info" sx={{ mt: 2 }}>
          No tasks found. Add your first task!
        </Alert>
      ) : (
        <TaskList
          tasks={tasks || []}
          onToggle={async (task) => { await toggleTask(task); }}
          onDelete={async (id) => { deleteTask(id); }}
          onUpdate={async (task) => { await updateTask(task); }}
        />
      )}
    </Box>
  );
};

export default App;
