import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Login from './components/Login';
import Signup from './components/Signup';
import { useTasks } from './hooks/useTasks';
import AddTask from './components/AddTask';
import TaskList from './components/TaskList';
import { Box, Typography, CircularProgress, Alert, Button, AppBar, Toolbar, Avatar } from '@mui/material';
import LogoutIcon from '@mui/icons-material/Logout';
import { Task } from './models/Task';

const App: React.FC = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [userName, setUserName] = useState('Anis Belkacem'); // Example username
  const { tasks, addTask, updateTask, toggleTask, deleteTask, isLoading } = useTasks();

  if (!isAuthenticated) {
    return (
      <Router>
        <Routes>
          <Route path="/login" element={<Login onLogin={() => setIsAuthenticated(true)} />} />
          <Route path="/signup" element={<Signup onSignup={() => {}} />} />
          <Route path="*" element={<Navigate to="/login" />} />
        </Routes>
      </Router>
    );
  }

  return (
    <Box sx={{ minHeight: '100vh', background: 'linear-gradient(135deg, #e0e7ff 0%, #f3f4f6 100%)' }}>
      {/* AppBar for the header */}
      <AppBar position="static" color="primary" elevation={2}>
        <Toolbar>
          <Avatar sx={{ bgcolor: '#1976d2', mr: 2 }}>{userName.charAt(0)}</Avatar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Welcome, <b>{userName}</b>
          </Typography>
          <Button
            variant="contained"
            color="secondary"
            startIcon={<LogoutIcon />}
            onClick={() => setIsAuthenticated(false)}
            sx={{ fontWeight: 600 }}
          >
            Logout
          </Button>
        </Toolbar>
      </AppBar>

      {/* Main Content */}
      <Box sx={{ maxWidth: 800, mx: 'auto', p: 3, mt: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom sx={{ mb: 4 }}>
          To-Do
        </Typography>
        <AddTask onAdd={async (title) => { await addTask({ title, completed: false }); }} />
        {isLoading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
            <CircularProgress />
          </Box>
        ) : tasks?.length === 0 ? (
          <Alert severity="info" sx={{ mt: 2 }}>
            No tasks found. Add your first task!
          </Alert>
        ) : (
          <TaskList
            tasks={tasks || []}
            onToggle={async (task) => { await toggleTask(task); }}
            onDelete={async (id) => { deleteTask(id); }}
            onUpdate={async (task) => { await updateTask(task); }}
          />
        )}
      </Box>
    </Box>
  );
};

export default App;
