import React, { useState } from 'react';
import { signup } from '../services/auth';
import { Box, Button, TextField, Typography, Alert } from '@mui/material';

const Signup: React.FC<{ onSignup: () => void }> = ({ onSignup }) => {
  const [form, setForm] = useState({
    firstName: '',
    lastName: '',
    username: '',
    email: '',
    dateOfBirth: '',
    password: '',
    confirmPassword: '',
  });
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setError('');
    setSuccess('');
    if (form.password !== form.confirmPassword) {
      setError('Passwords do not match');
      return;
    }
    try {
      await signup(
        form.username,
        form.password,
        form.firstName,
        form.lastName,
        form.email,
        form.dateOfBirth
      );
      setSuccess('Signup successful! You can now log in.');
      onSignup();
    } catch (err: any) {
      setError(err.response?.data || 'Signup failed');
    }
  };

  return (
    <Box component="form" onSubmit={handleSubmit} sx={{ maxWidth: 400, mx: 'auto', mt: 8 }}>
      <Typography variant="h5" mb={2}>Sign Up</Typography>
      {error && <Alert severity="error">{error}</Alert>}
      {success && <Alert severity="success">{success}</Alert>}
      <TextField label="First Name" name="firstName" fullWidth margin="normal" value={form.firstName} onChange={handleChange} />
      <TextField label="Last Name" name="lastName" fullWidth margin="normal" value={form.lastName} onChange={handleChange} />
      <TextField label="Username" name="username" fullWidth margin="normal" value={form.username} onChange={handleChange} />
      <TextField label="Email" name="email" type="email" fullWidth margin="normal" value={form.email} onChange={handleChange} />
      <TextField label="Date of Birth" name="dateOfBirth" type="date" fullWidth margin="normal" value={form.dateOfBirth} onChange={handleChange} InputLabelProps={{ shrink: true }} />
      <TextField label="Password" name="password" type="password" fullWidth margin="normal" value={form.password} onChange={handleChange} />
      <TextField label="Confirm Password" name="confirmPassword" type="password" fullWidth margin="normal" value={form.confirmPassword} onChange={handleChange} />
      <Button type="submit" variant="contained" fullWidth sx={{ mt: 2 }}>Sign Up</Button>
    </Box>
  );
};

export default Signup;
